import { useHistory, useParams } from "react-router-dom";
import { useEffect, useState } from "react/cjs/react.development";
import airlineService from "../services/airline.service";



export default function UpdateOffer(){

    const [offerFrom , setOfferFrom] = useState('')
    const [offerTo , setOfferTo] = useState('')
    const [discount , setDiscount] = useState('')
    const {offerId} = useParams();

    const history = useHistory();


    const updateOffer = (id) => {
        const offer = {offerFrom , offerTo , discount}
        airlineService.updateOffer(offer)
                .then(response => {
                    console.log('Offer data updated successfully', response.data);
                    history.push('/owner');
                })
                .catch(error => {
                    console.log('Something went wrong', error);
                }) 
        } 

      useEffect(() => {
        if (offerId) {
            airlineService.getOfferById(offerId)
                .then(response => {
                    setOfferFrom(response.data.offerFrom);
                    setOfferTo(response.data.offerTo);
                    setDiscount(response.data.discount);
                })
                .catch(error => {
                    console.log('Something went wrong', error);
                })
        }
    }, [])

return(
    <div>
        <form>
        <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="offerto"
                        value={offerTo}
                        onChange={(e) => setOfferTo(e.target.value)}
                        placeholder="ENTER TO.."
                    />

                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="offerfom"
                        value={offerFrom}
                        onChange={(e) => setOfferFrom(e.target.value)}
                        placeholder="ENTER FROM.."
                    />
                </div>

                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="discount"
                        value={discount}
                        onChange={(e) => setDiscount(e.target.value)}
                        placeholder="ENTER DISCOUNT.."
                    />
                </div>
                <div >
                    <button onClick={(e)=>updateOffer(e)}className="btn btn-primary">UPDATE OFFER</button>
                </div>
                </div>
        </form>


    </div>
)





}
