                    <tr>
                        <th>FLIGHT NAME</th>
                        <th>FROM</th>
                        <th>TO</th>
                        <th>SEATS AVAILABLE</th>
                        <th>ECONOMIC PRICE</th>
                        <th>BUSINESS PRICE</th>
                        <th>FIRST CLASS PRICE</th>
                        <th>DEPARTURE DATE</th>
                        <th>ARRIVAL DATE</th>
                        <th>DEPARTURE TIME</th>
                        <th>ARRIVAL TIME</th>
                        <th></th>
                    </tr>
                </thead>
                <tbody>
                {
            flights.map(flight => (
              <tr key={flight.flightId}>
                <td>{flight.flightName}</td>
                <td>{flight.flightFrom}</td>
