import { useState } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { useEffect } from "react/cjs/react.development";
import employeeService from "../services/airline.service";

const RegistrationPage = () => {
    const[email, setEmail] = useState('');
    const[name, setName] = useState('');
    const[phoneNo, setPhoneNo] = useState('');
    const[password, setPassword] = useState('');
    const[confirmPassword, setConfirmPassword] = useState('');
    const[securityQ, setSecurityQ] = useState('');
    const[answer, setAnswer] = useState('');
    const {id} = useParams();



    const saveEmployee = (e) => {
        e.preventDefault();

        const employee = {name, location, department, id};

            //create
            employeeService.createuser(employee)
            .then(response => {
                console.log("employee added successfully", response.data);
                history.push("/");
            })
            .catch(error => {
                console.log('something went wroing', error);
            })
    }


    return(
        <div className="container">
            <h3>Register</h3>
            <hr/>
            <form>
            <div className="form-group">
                    <input 
                        type="email" 
                        className="form-control col-4"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter Email"
                    />

                </div>
                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="name"
                        value={name}
                        onChange={(e) => setName(e.target.value)}
                        placeholder="Enter Name"
                    />

                </div>
                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="phoneNo"
                        value={phoneNo}
                        onChange={(e) => setPhoneNo(e.target.value)}
                        placeholder="Enter Phone No"
                    />

                </div>
                <div className="form-group">
                    <input 
                        type="password" 
                        className="form-control col-4"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter Password"
                    />

                </div>
                <div className="form-group">
                    <input 
                        type="password" 
                        className="form-control col-4"
                        id="confirmPassword"
                        value={confirmPassword}
                        onChange={(e) => setConfirmPassword(e.target.value)}
                        placeholder="Confirm Password"
                    />

                </div>
                <div class="form-group">
    <label for="exampleFormControlSelect1">Select Security Question</label>
    <select class="form-control" id="exampleFormControlSelect1">
      <option>What's your Pet's name ?</option>
      <option>What's your place of Birth ?</option>
      <option>What's your favourite Movie ?</option>
    </select>
  </div>
                <div className="form-group">
                    <input 
                        type="text" 
                        className="form-control col-4"
                        id="name"
                        value={answer}
                        onChange={(e) => setAnswer(e.target.value)}
                        placeholder="Enter Security Question Answer"
                    />

                </div>
                <div class="form-check">
                    <input class="form-check-input" type="radio" name="flexRadioDefault" 
                    id="flexRadioDefault1"/>
                    <label class="form-check-label" for="flexRadioDefault1">
                        Admin
                    </label>
                </div>

<div class="form-check">
  <input class="form-check-input" type="radio" name="flexRadioDefault" id="flexRadioDefault2" checked/>
  <label class="form-check-label" for="flexRadioDefault2">
    User
  </label>
</div>
                <div >
                    <button  className="btn btn-primary">Register</button>
                </div>
            </form>
            <hr/>
            <Link to="/">Back to List</Link>
        </div>
    )
}

export default RegistrationPage;
