import { useState } from "react";
import { Link, useHistory, useParams } from "react-router-dom";
import { useEffect } from "react/cjs/react.development";
import airlineService from "../services/airline.service";

const LoginPage = () => {
    const[email, setEmail] = useState('');
    const[password, setPassword] = useState('');

   const[users,setUsers] = useState([])
    const history = useHistory();
    const {id} = useParams();

    const loginUser = (e) => {
        e.preventDefault();

        const user = {email,password,id};
            users.map((user)=>{
            if(type === "user" && email === user.email && password === user.password){
                    console.log("User Login successfully");
                    history.push("/UserPage");
            // }else if(type === "admin" && email === user.email && password === user.password){
            //     console.log("Admin Login successfully");
            //     history.push("/AdminPage");
            // }
            // else if(type === "owner" && email === user.email && password === user.password){
            //     console.log("Owner Login successfully");
            //     history.push("/OwnerPage");

            }

        });

        }

    useEffect(() => {
        airlineService.getAllUsers()
        .then(response => {
          console.log('Printing Users data', response.data);
          setUsers(response.data);
        })
        .catch(error => {
          console.log('Something went wrong', error);
        }) 
        }, [])
    return(
        <div className="container">
            <h3>Login</h3>
            <hr/>
            <form>
            <div className="form-group">
                    <input 
                        type="email" 
                        className="form-control col-4"
                        id="email"
                        value={email}
                        onChange={(e) => setEmail(e.target.value)}
                        placeholder="Enter your Email"
                    />

                </div>
                <div className="form-group">
                    <input 
                        type="password" 
                        className="form-control col-4"
                        id="password"
                        value={password}
                        onChange={(e) => setPassword(e.target.value)}
                        placeholder="Enter your Password"
                    />

                </div>

                <div >
                    <br></br>
                    <button onClick={(e) => loginUser(e)} className="btn btn-primary">Submit</button>
                </div>
            </form>
            <hr/>
            <Link to="/forgotpassword"> Forgot Password</Link>
            <br></br>

            <Link to="/register">Not Registered Yet? Register Now</Link>
        </div>
    )
}

export default LoginPage;
