import httpClient from "../http-common";


//FLIGHTS
const getAllFlights = () =>{
    return httpClient.get('/allflight');
}

const createFlight = flight =>{
    return httpClient.post('/createflight', flight)
}

//USERS
const getAllUsers = () =>{
    return httpClient.get('/alluser');
}

const createUser = user =>{
    return httpClient.post('/createuser' , user);
}


//OFFERS
const getAllOffers=()=>{
    return httpClient.get('/alloffers');
}

const createOffer = offer =>{
    return httpClient.post('/createoffer', offer);
}




const getAll = () => {
    return httpClient.get('/employees');
}

const create = data => {
    return httpClient.post("/employees", data);
}

const get = id => {
    return httpClient.get(`/employees/${id}`);
}

const update = data => {
    return httpClient.put('/employees', data);
}

const remove = id => {
    return httpClient.delete(`/employees/${id}`);
}
export default { getAll, create, get, update, remove , 
    getAllFlights , getAllUsers, createFlight, createUser, createOffer,getAllOffers};
